mapper.select:
select date_format(l.login_time, '%m-%d') days, count(1) count from ( select * from admin_login_log where date_sub(curdate(), interval 10 day) <= date(login_time) ) as l where 1 = 1
and l.username = #{username}
group by days

select id, username, login_time, location, ip, log_system, browser
from admin_login_log
where id = #{id,jdbcType=INTEGER}

select id, username, login_time, location, ip, log_system, browser
from admin_login_log


    and  username like concat('%',#{username},'%')



     and login_time >=#{startTime}



   and  login_time <=#{endTime}


ORDER BY login_time desc
